import React from 'react';
import { useAsync } from "react-async"
import MapsPresenter from './MapsPresenter';
import apiService from '../../service/apiService.js';
import {connect} from 'react-redux';

const MapsContainer = (props) => {
    let { data: vesselList } = useAsync({ promiseFn: apiService.callVesselList, locationId:105160 });
    let position = [35.0728, 128.8060];
    let changePosition;
    let zoom = 14;
    const multiPolygon = [
        [
            [35.07, 128.8075],
            [35.072, 128.8060],
            [35.075, 128.8060],
            [35.075, 128.8090],
            [35.072, 128.8090],
        ],
    ]

    const purpleOptions = { color: 'purple' }
    
    if(props.vesselList){
        vesselList.data = props.vesselList;

        let latitude = 0;
        let longitude = 0;

        vesselList.data.forEach(vessel => {
            latitude += vessel.latitude;
            longitude += vessel.longitude
        });

        latitude /= vesselList.data.length;
        longitude /= vesselList.data.length;

        changePosition = [latitude, longitude];
    }

    return (
        <MapsPresenter
            position={position}
            changePosition={changePosition}
            zoom={zoom}
            multiPolygon={multiPolygon}
            purpleOptions={purpleOptions}
            vesselList={vesselList}
        />
    );
};

function getVesslList(state) {
    return {
        vesselList : state
    }
}

export default connect(getVesslList)(MapsContainer);
