import React from 'react';
import { useAsync } from "react-async"
import SelectBoxPresenter from './SelectBoxPresenter';
import apiService from '../../service/apiService.js'
import {connect} from 'react-redux';

const SelectBoxContainer = (props) => {
    const { data: portList, error, isLoading } = useAsync({ promiseFn: apiService.callPortList });

    const changePort = (e) => {
        console.log(e);
        const { data: vesselList, error, isLoading } = useAsync({ promiseFn: apiService.callVesselList, locationId:105160 });
        props.dispatch({type:'setVessel'})
    }

    return (
        <SelectBoxPresenter
            portList={portList}
            changePort = {changePort}
        />
    );
};

function setVesslList(state) {
    return {
        state : state
    }
}

export default connect(setVesslList)(SelectBoxContainer);
