import {React, useState} from 'react';
import '../../styles/scss/Maps.scss'
import { MapContainer, TileLayer, Polygon, Circle, useMapEvents} from "react-leaflet";

function LocationMarker() {
  const [position, setPosition] = useState(null)
  const map = useMapEvents({
    click() {
      map.locate()
      // setPosition([35.0728, 128.8060])
      map.flyTo([35.0728, 128.8060], map.getZoom())
    },
  })

  return null;
}

const MapsPresenter = (props) => {
  const {
    position,
    changePosition,
    zoom,
    purpleOptions,
    vesselList
  } = props;

  return (
    <div>
      <MapContainer
        className="Maps"
        center={changePosition ? changePosition : position}
        zoom={zoom}
      >
        <TileLayer
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {
          vesselList ? vesselList.data.map((vessel, index) => {
            const multiPolygon = [
              [
                [vessel.latitude + 0.0012 * Math.sin(vessel.heading), vessel.longitude + 0.0012 * Math.cos(vessel.heading)],
                [vessel.latitude, vessel.longitude],
                [vessel.latitude, vessel.longitude],
                [vessel.latitude, vessel.longitude],
                [vessel.latitude, vessel.longitude],
              ],
            ]
            console.log("heading : " + vessel.heading);
            console.log("sin : " + Math.sin(vessel.heading * 3.14 / 180));
            return (
              // <Polygon key={index} pathOptions={purpleOptions} positions={multiPolygon} />
              <Circle key={index} center={[vessel.latitude, vessel.longitude]} radius={60} />
            )
          }) : <div></div>
        }
        <LocationMarker/>
      </MapContainer>
    </div>
  );
}

export default MapsPresenter;
